mapper.select:
SELECT sc.schedule_id AS scheduleId, sc.semester_id AS semesterId, sc.schedule_type AS scheduleType, sc.start_datetime AS startDatetime, sc.end_datetime AS endDatetime, sc.description, sc.created_at AS createdAt FROM schedule sc WHERE sc.start_datetime <= #{end} AND sc.end_datetime >= #{start}
AND sc.semester_id = #{semesterId}
ORDER BY sc.start_datetime ASC

SELECT COALESCE((
SELECT CASE WHEN NOW() BETWEEN sc.start_datetime AND sc.end_datetime THEN 1 ELSE 0 END
FROM schedule sc
WHERE sc.semester_id = #{semesterId}
AND TRIM(sc.schedule_type) = TRIM(#{scheduleType})
),0)
