mapper.select:
SELECT c.status, c.course_id, c.course_code, c.title, c.classroom, c.type, c.grade, s.year, s.semester, c.time, c.credit, c.max_std, c.rem_std, c.user_id
FROM course c JOIN semester s ON s.semester_id = c.semester_id WHERE s.year = #{year} AND c.status = '승인' AND s.semester = #{semester}
AND c.type LIKE CONCAT(#{type}, '%')
AND c.grade = #{grade}
AND c.title LIKE CONCAT('%', #{keyword}, '%')
AND c.user_id IN
#{id}
AND c.type LIKE '전공%'
ORDER BY c.title

SELECT c.course_id, c.title, c.user_id, c.classroom, c.time, c.type
FROM enrollment e
JOIN course c
ON e.course_id = c.course_id
WHERE e.user_id = #{userId}
AND c.semester_id = #{sid}
AND c.time LIKE '${time}%'
ORDER BY c.time ;

SELECT c.course_id, c.title, c.user_id, c.classroom, c.time, c.type
FROM course c
WHERE c.user_id = #{userId}
AND c.semester_id = #{sid}
AND c.time LIKE '${time}%'
ORDER BY c.time ;
