mapper.select:
SELECT a.app_id, a.user_id, a.status, a.reason, a.created_at, sc.schedule_type, sm.year, sm.semester FROM application a JOIN schedule sc ON a.schedule_id = sc.schedule_id JOIN semester sm ON sc.semester_id = sm.semester_id WHERE sm.year = #{year}
AND sm.semester = #{semester}
AND sc.schedule_type = #{scheduleType}
ORDER BY a.created_at DESC

mapper.update:
UPDATE application
SET status = #{status}
WHERE app_id = #{appId}
